document.addEventListener("DOMContentLoaded",(function(){document.getElementById("burger").addEventListener("click",(function(){document.querySelector("header").classList.toggle("open"),document.querySelector("body").classList.toggle("stop-scrolling")}))}));const menuLinks=document.querySelectorAll(".nav__link");menuLinks.forEach((e=>{e.addEventListener("click",(()=>{document.querySelector("header").classList.remove("open"),document.querySelector("body").classList.remove("stop-scrolling")}))}));